import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { db } from "../firebaseConfig"; // Assuming you're using a firebase config file
import { collection, query, where, getDocs } from "firebase/firestore";

const ForumSubtopics = () => {
  const [discussionCounts, setDiscussionCounts] = useState({});
  const subtopics = [
    { id: "1", title: "Common Pet Diseases and Treatments", description: "Discuss common illnesses and remedies.", url: "data:image/jpeg;base64," },
    { id: "2", title: "Pet Nutrition and Diet", description: "Share tips on feeding your pets the right way.", url: "data:image/jpeg;base64," },
    { id: "3", title: "Training Tips and Tricks", description: "Learn and share methods to train your pets.", url: "data:image/jpeg;base64," },
    { id: "4", title: "Pet Adoption Stories", description: "Share your adoption stories or seek advice.", url: "data:image/jpeg;base64," },
    { id: "5", title: "Grooming Advice", description: "Discuss grooming techniques and tools.", url: "data:image/jpeg;base64," },
    { id: "6", title: "Pet Loss Support", description: "Support each other through the loss of a beloved pet.", url: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTD8We2c3gPw1rDiJeaLkcoAmarq8SS9troGA&s" },
  ];

  // Function to fetch discussion counts for each subforum
  const fetchDiscussionCounts = async () => {
    try {
      const counts = {};
      for (const subtopic of subtopics) { // Changed 'subtopics' to 'subtopic'
        // Query to fetch discussions based on topicId
        const discussionsRef = collection(db, "discussions");
        const q = query(discussionsRef, where("topicId", "==", subtopic.id));
        const querySnapshot = await getDocs(q);
        
        // Store the count of discussions for this subtopic
        counts[subtopic.id] = querySnapshot .size; // number of discussions for this topicId
      }

      // Update state with the discussion counts
      setDiscussionCounts(counts);
    } catch (error) {
      console.error("Error fetching discussion counts:", error);
    }
  };

  // Use useEffect to fetch the data when the component mounts
  useEffect(() => {
    fetchDiscussionCounts();
  }, []);

  return (
    <div className="container mt-4">
      <h1 className="mb-4 text-center">Sub Forums</h1>
      <div className="row">
        {subtopics.map((topic) => (
          <div className="col-md-6 col-lg-4 mb-4" key={topic.id}>
            <div className="card shadow-sm h-100">
              <img className="card-img-top" src={topic.url} alt="Card image cap" />
              <div className="card-body d-flex flex-column">
                <h5 className="card-title">{topic.title}</h5>
                <p className="card-text text-muted">{topic.description}</p>
                <p className="text-muted">{discussionCounts[topic.id] || 0} Discussions</p> {/* Show number of discussions */}
                <Link to={`/Home/ForumDiscussion/${topic.id}`} className="btn btn-primary mt-auto">
                  View Discussions
                </Link>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ForumSubtopics;
